import { useEffect, useRef, useState } from "react";
import { CSSTransition, TransitionGroup } from "react-transition-group";

const HistoryItem = ({
  length, 
  index, 
  word,
}: {
  length: number, 
  index: number, 
  word: string,
}) => {

  const itemStyle: any = {
    opacity: 1 / Math.sqrt(length - index),
    fontSize: `${2 / Math.cbrt(length - index)}em`,
    // color: "white",
    transition: "all 1s ease-out",
  };

  return (
    <div 
      key={index}
      style={itemStyle}
    >
      {word}
    </div>
  )
};

export const History = ({history}: {history: Set<string>}) => (
  <TransitionGroup>
    <CSSTransition
      classNames="slide"
      timeout={{ enter: 300 }}
      key={Math.random()}
    >
      <div className="history">
        { 
          Array.from(history).map((word, index) => (
            <HistoryItem 
              key={index}
              length={history.size}
              index={index}
              word={word}
            />
          ))
        }
      </div>
      </CSSTransition>
    </TransitionGroup>
);
