import React, { useEffect, useState } from 'react';
import { History } from './components/History';
import { WordInput } from './components/WordInput';

import './App.css';

import { socket } from './socket';

const PlayerPanel = ({startFunc}: {startFunc: Function}) => { 
  return (
    <div>
      {

      }
    </div>
  );
};


const GameSetup = ({startFunc}: {startFunc: Function}) => { 
  return (
    <button
      className='startButton'
      onClick={() => startFunc()}
    >
      START GAME
    </button>
  );
};

const GameInProgress = ({turn}: {turn: boolean}) => { 

  const [history, setHistory] = useState<Set<string>>(new Set());

  useEffect(()=>{

    // console.log(socket.id);

    socket.on("turnInfo", (bcHistory, playerWithTurn) => {
      setHistory(new Set(bcHistory));

      console.log(socket.id, playerWithTurn);
    });

    // return () => {
    //   socket.off("historyBroadcast", (bcHistory) => {
    //     setHistory(new Set(bcHistory));           
    //   });
    // };
  }, []);

  const emitSubmit = (newHistory: string[]) => {
    socket.emit("submit", newHistory);
    setHistory(new Set(newHistory));
  };

  return (
    <div className="mainContent">
      <History history={history}/>
      <WordInput 
        history={history}
        emitFunc={emitSubmit}
        yourTurn={turn}
      />
    </div>
  );
};

const App = () => {

  const [gameStarted, setGameStarted] = useState<boolean>(false);
  const [yourTurn, setYourTurn] = useState<boolean>(false);

  // if this client starts game
  const startGame = () => { 
    socket.emit("start");
    setGameStarted(true);
    setYourTurn(true);
  };

  // if other client starts game
  useEffect(() => {
    socket.on("gameStarted", () => {
      setGameStarted(true);
    });
  }, []);

  return (
    <div className="app">
      <PlayerPanel />
      {
        gameStarted
        ? <GameInProgress turn={yourTurn} />
        : <GameSetup startFunc={startGame} />
      }
    </div>
  );
}

export default App;
