import { useEffect } from "react";

const HistoryItem = ({
  length, 
  index, 
  word,
}: {
  length: number, 
  index: number, 
  word: string,
}) => {

  const opacity = 1 / Math.sqrt(length - index);
  const fontSize = `${2 / Math.cbrt(length - index)}em`;

  const itemStyle: any = {
    opacity: 1 / Math.sqrt(length - index),
    fontSize: `${2 / Math.cbrt(length - index)}em`,
    color: "white",

    position: "relative",
    width: "max-content", height: "1em",
    display: "inline-block",
    transition: "all .3s ease-out",
    backgroundColor: "red",
  };

  let truncated = false;

  useEffect(() => {
    setTimeout(() => {
      truncated = true;
    }, 1000)
  }, [])

  const spanStyle: any = {
    position: "absolute",
    width: "max-content",
    top: truncated ? "0" : "1em",
  };

  return (
    <div 
      key={index}
      style={itemStyle}
    >
      <div style={spanStyle}>
        {word}
      </div>
    </div>
  )
};

export const History = ({history}: {history: Set<string>}) => (
  <div className="history">
    { 
      Array.from(history).map((word, index) => (
        <HistoryItem 
          key={index}
          length={history.size}
          index={index}
          word={word}
        />
      ))
    }
  </div>
);
