import { createContext, useState } from 'react';

// interface GameContextType {
//   players: string[];
//   turnClientId: string;
//   yourTurn: boolean;
//   history: Set<string>;
// };

interface GameContextType {
  playerState: [string[], React.Dispatch<React.SetStateAction<string[]>>];
  turnClientState: [string, React.Dispatch<React.SetStateAction<string>>];
  yourTurnState: [boolean, React.Dispatch<React.SetStateAction<boolean>>];
  historyState: [Set<string>, React.Dispatch<React.SetStateAction<string>>];
};

const initGameValues: GameContextType = {
  playerState: [],
  turnClientState: "",
  yourTurnState: false,
  historyState: new Set(),
};

export const GameContext = createContext(initGameValues);

export const GameProvider = ({ children }: any) => {

  // const [players, setPlayers] = useState<string[]>([]);
  // const [turnClientId, setTurnClientId] = useState<string>("");
  // const [yourTurn, setYourTurn] = useState<boolean>(false);
  // const [history, setHistory] = useState<Set<string>>(new Set()); 

  const playerState     = useState<string[]>([]);
  const turnClientState = useState<string>("");
  const yourTurnState   = useState<boolean>(false);
  const historyState    = useState<Set<string>>(new Set()); 

  // const contextValue: GameContextType = {
  //   players,
  //   turnClientId,
  //   yourTurn,
  //   history,
  // };

  const contextValue: GameContextType = {
    playerState,
    turnClientState,
    yourTurnState,
    historyState,
  };

  return (
    <GameContext.Provider value={contextValue}>
      {children}
    </GameContext.Provider>
  )
};
