import { useContext, useEffect, useRef, useState } from "react";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import { GameContext } from "../context/game";

const HistoryItem = ({
  word,
  user,
}: {
  word: string,
  user: string,
}) => {

  return (
    <div className="historyRow">
      <div style={{marginLeft: "25%", width: "40px", textAlign: "center", fontFamily: "'Rockwell'", fontWeight: "bold", backgroundColor: "#282828"}}>P1</div>
      <div style={{flexGrow: 1, paddingLeft: "10px", fontFamily: "'Rockwell'", backgroundColor: "#333"}}>{word}</div>
    </div>
  );
};

export const History = () => {

  const {
    // playerState: [players, setPlayers], 
    // turnClientState: [turnClientId, setTurnClientId], 
    historyState: [history, setHistory],
  } = useContext(GameContext);

  return (
    <div className="history">
      <TransitionGroup>
        { 
          Array.from(history).map(([word, { user }], index) => (
            <CSSTransition
              key={word}
              classNames="slide"
              timeout={{ enter: 1000 }}
            >
              <HistoryItem 
                key={`history-${index}`}
                word={word}
                user={user}
              />
            </CSSTransition>
          ))
        }
      </TransitionGroup>
    </div>
  )
};
