import { useEffect, useRef, useState } from "react";
import { Transition } from "react-transition-group";

const HistoryItem = ({
  length, 
  index, 
  word,
}: {
  length: number, 
  index: number, 
  word: string,
}) => {

  const nodeRef = useRef(null);

  const itemStyle: any = {
    opacity: 1 / Math.sqrt(length - index),
    fontSize: `${2 / Math.cbrt(length - index)}em`,
    color: "white",
    backgroundColor: "magenta",
    // transform: "translateY(-1em)", 
    transition: "all .5s ease-out",
  };

  return (
    <Transition in={true} timeout={300}>
      {state => (
        // <ModalBackdrop duration={duration} state={state}>
        //   <Sidebar duration={duration} state={state}>
        //     {isOpen && (
        //       <div>
        //         <h2>I am here!</h2>
        //         <button onClick={onCloseModalClick}>Close Menu!</button>
        //       </div>
        //     )}
        //   </Sidebar>
        // </ModalBackdrop>
        <div 
          key={index}
          style={itemStyle}
        >
          {word}
        </div>
      )}
      {/* <div 
        key={index}
        style={itemStyle}
      >
        {word}
      </div> */}
    </Transition>
  )
};

export const History = ({history}: {history: Set<string>}) => (
  <div className="history">
    { 
      Array.from(history).map((word, index) => (
        <HistoryItem 
          key={index}
          length={history.size}
          index={index}
          word={word}
        />
      ))
    }
  </div>
);
