import { useContext, useEffect, useRef, useState } from "react";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import { GameContext } from "../context/game";
import { SocketContext } from "../context/socket";

const HistoryItem = ({
  word,
  user: {userId, userNumber},
}: {
  word: string,
  user: {userId: string, userNumber: number}
}) => {

  const socket = useContext(SocketContext);

  return (
    (userId === socket.id)
    ? (
        <div className="historyRowPlayer">
          <div style={{marginLeft: "25%", width: "40px", lineHeight: "20px", textAlign: "center", fontFamily: "Kameron", fontWeight: "bold", backgroundColor: "#00B050", borderRight: "3px solid #222", color: "#000"}}>
            P{userNumber + 1}
          </div>
          <div className="historyWord" style={{backgroundColor: "#24d574", color: "#000"}}>
            {word}
          </div>
        </div>
      )
    : (
        <div className="historyRow">
          <div style={{marginLeft: "25%", width: "40px", lineHeight: "20px", textAlign: "center", fontFamily: "Kameron", fontWeight: "bold", backgroundColor: "#282828", borderRight: "3px solid #222", color: "#fff"}}>
            P{userNumber + 1}
          </div>
          <div className="historyWord" style={{backgroundColor: "#313131", color: "#fff"}}>
            {word}
          </div>
        </div> 
      )
  );
};

export const History = () => {

  const {
    // playerState: [players, setPlayers], 
    // turnClientState: [turnClientId, setTurnClientId], 
    historyState: [history, setHistory],
  } = useContext(GameContext);

  return (
    <div className="history">
      <TransitionGroup>
        { 
          Array.from(history).map(([word, user], index) => (
            <CSSTransition
              key={word}
              classNames="slide"
              timeout={{ enter: 1000 }}
            >
              <HistoryItem 
                key={`history-${index}`}
                word={word}
                user={user}
              />
            </CSSTransition>
          ))
        }
      </TransitionGroup>
    </div>
  )
};
