import { useContext, useEffect, useRef, useState } from "react";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import { GameContext } from "../context/game";
import { SocketContext } from "../context/socket";

const HistoryItem = ({
  word,
  user,
}: {
  word: string,
  user: string,
}) => {

  const socket = useContext(SocketContext);

  return (
    (user === socket.id)
    ? (
        <div className="historyRow">
          <div style={{marginLeft: "25%", width: "40px", lineHeight: "20px", textAlign: "center", fontFamily: "Kameron", fontWeight: "bold", backgroundColor: "#282828", borderRight: "3px solid #222"}}>P1</div>
          <div style={{flexGrow: 1, paddingLeft: "10px", lineHeight: "20px", fontSize: ".9em", fontFamily: "Source Code Pro", backgroundColor: "#313131"}}>{word}</div>
        </div>
      )
    : <></>
  );
};

export const History = () => {

  const {
    // playerState: [players, setPlayers], 
    // turnClientState: [turnClientId, setTurnClientId], 
    historyState: [history, setHistory],
  } = useContext(GameContext);

  return (
    <div className="history">
      <TransitionGroup>
        { 
          Array.from(history).map(([word, { user }], index) => (
            <CSSTransition
              key={word}
              classNames="slide"
              timeout={{ enter: 1000 }}
            >
              <HistoryItem 
                key={`history-${index}`}
                word={word}
                user={user}
              />
            </CSSTransition>
          ))
        }
      </TransitionGroup>
    </div>
  )
};
