import React, { useEffect, useState } from 'react';
import { History } from './components/History';
import { WordInput } from './components/WordInput';

import './App.css';

import { socket } from './socket';

const PlayerPanel = ({ players, turnId }: { players: string[], turnId: string }) => { 
  return (
    <div className="playerPanel">
      <div style={{lineHeight: "2em", fontSize: ".8em"}}>
        PLAYERS ({players.length}):
      </div>
      {players.map(playerName => 
        <div style={{fontStyle: "italic", fontSize: ".8em", color: "green"}}>{playerName}</div>
      )}
    </div>
  );
};

const GameSetup = ({ startFunc }: { startFunc: Function }) => { 
  return (
    <button
      className="startButton"
      onClick={() => startFunc()}
    >
      START GAME
    </button>
  );
};

const GameInProgress = ({ 
    turnState: [turn, setTurn],
    setTurnClientId,
  }: { 
    turnState: [boolean, any],
    setTurnClientId: any,
  }) => { 

  const [history, setHistory] = useState<Set<string>>(new Set()); 

  useEffect(()=>{

    // console.log(socket.id);

    socket.on("turnInfo", (bcHistory, turnId) => {
      setHistory(new Set(bcHistory));
      setTurnClientId(turnId);
      if (turnId === socket.id) {
        setTurn(true);
      };
    });

    // return () => {
    //   socket.off("historyBroadcast", (bcHistory) => {
    //     setHistory(new Set(bcHistory));           
    //   });
    // };
  }, []);

  const emitSubmit = (newHistory: string[]) => {
    socket.emit("submit", newHistory);
    setHistory(new Set(newHistory));
    setTurn(false);
  };
  
  return (
    <div className="mainContent">
      <History history={history}/>
      <WordInput 
        history={history}
        emitFunc={emitSubmit}
        yourTurn={turn}
      />
    </div>
  );
};


const App = () => {

  const [players, setPlayers] = useState<string[]>([]);
  const [gameStarted, setGameStarted] = useState<boolean>(false);
  const [turnClientId, setTurnClientId] = useState<string>("");
  const [yourTurn, setYourTurn] = useState<boolean>(false);

  useEffect(() => {
    // if other client joins lobby
    socket.on("playersChange", newPlayers => {
      setPlayers(newPlayers);
    });

    // if game has already started when joining
    // socket.on("alreadyStarted", () => {
    //   setGameStarted(true);
    // });

    // if other client starts game
    socket.on("gameStarted", () => {
      setGameStarted(true);
    });

    // if game is ended
    socket.on("gameEnded", () => {
      setGameStarted(false);
    });
  }, []);

  // start game
  const startGame = () => { 
    socket.emit("start");
    setYourTurn(true);
  };

  return (
    <div className="app">
      <PlayerPanel players={players} turnId={turnClientId}/>
      {
        gameStarted 
        ? <GameInProgress turnState={[yourTurn, setYourTurn]} setTurnClientId={setTurnClientId}/>
        : <GameSetup startFunc={startGame} />
      }
    </div>
  );
}

export default App;
