export const History = ({history}: {history: Set<string>}) => (
  <div className="history">
    { 
      Array.from(history).map((word, i) => {
        const sizeFactor = 2 / Math.cbrt(history.size - i);
        const opacityFactor = 1 / Math.sqrt(history.size - i);

        return (
          <div 
            style={{
              fontSize: `${sizeFactor}em`,
              opacity: opacityFactor,
            }}
          >
            {word}
          </div>
        )
      })
    }
  </div>
);
