import { useEffect, useState } from "react";
import { CSSTransition } from "react-transition-group";

const HistoryItem = ({
  length, 
  index, 
  word,
}: {
  length: number, 
  index: number, 
  word: string,
}) => {

  const nodeRef = useRef(null);

  const itemStyle: any = {
    opacity: 1 / Math.sqrt(length - index),
    fontSize: `${2 / Math.cbrt(length - index)}em`,
    color: "white",
    backgroundColor: "magenta",
    // transform: "translateY(-1em)", 
    transition: "all .5s ease-out",
  };

  return (
    <CSSTransition>
      <div 
        key={index}
        style={itemStyle}
      >
        {word}
      </div>
    </CSSTransition>
  )
};

export const History = ({history}: {history: Set<string>}) => (
  <div className="history">
    { 
      Array.from(history).map((word, index) => (
        <HistoryItem 
          key={index}
          length={history.size}
          index={index}
          word={word}
        />
      ))
    }
  </div>
);
