import { useEffect, useRef, useState } from "react";

const HistoryItem = ({
  length, 
  index, 
  word,
}: {
  length: number, 
  index: number, 
  word: string,
}) => {

  const itemStyle: any = {
    opacity: 1 / Math.sqrt(length - index),
    fontSize: `${2 / Math.cbrt(length - index)}em`,
    color: "white",
    backgroundColor: "magenta",
    // transition: "all .5s ease-out",
  };

  return (
    <div 
      key={index}
      style={itemStyle}
    >
      {word}
    </div>
  )
};

export const History = ({history}: {history: Set<string>}) => (
  <div className="history">
    { 
      Array.from(history).map((word, index) => (
        <HistoryItem 
          key={index}
          length={history.size}
          index={index}
          word={word}
        />
      ))
    }
  </div>
);
