import React, { useEffect, useState } from 'react';
import { History } from './components/History';
import { WordInput } from './components/WordInput';

import './App.css';

import { socket } from './socket';

const App = () => {

  const [history, setHistory] = useState<Set<string>>(new Set());
  const [activePlayerIndex, setActivePlayerIndex] = useState<number>(0);

  useEffect(()=>{
    socket.on("historyBroadcast", (bcHistory) => {
      setHistory(new Set(bcHistory));           
    });

    // return () => {
    //   socket.off("historyBroadcast", (bcHistory) => {
    //     setHistory(new Set(bcHistory));           
    //   });
    // };
  }, [])

  const emitHistory = (newHistory: string[]) => {
    socket.emit("history", newHistory);
    setHistory(new Set(newHistory));
  };

  return (
    <div className="app">
      <div style={{top: "25%", position: "relative", width: "50%", height: "auto"}}>
        <History history={history}/>
        <WordInput 
          history={history}
          emitFunc={emitHistory}
        />
      </div>
    </div>
  );
}

export default App;
