import { useContext, useEffect, useRef, useState } from "react";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import { GameContext } from "../context/game";

const HistoryItem = ({
  wordObj,
}: {
  // word: string,
  wordObj: {word: string, user: string},
}) => {


  const itemStyle: any = {
    lineHeight: "1em",
    color: "white",
    transition: "all 300ms ease-out",
    width: "max-content",
  };

  return (
    <div style={itemStyle}>
      {wordObj.word}
    </div>
  );
};

export const History = () => {

  const {
    // playerState: [players, setPlayers], 
    // turnClientState: [turnClientId, setTurnClientId], 
    historyState: [history, setHistory],
  } = useContext(GameContext);

  return (
    <div className="history">
      <TransitionGroup>
        { 
          Array.from(history).map((wordObj, index) => (
            <CSSTransition
              key={wordObj.word}
              classNames="slide"
              timeout={{ enter: 1000 }}
            >
              <HistoryItem 
                key={`history-${index}`}
                wordObj={wordObj}
              />
            </CSSTransition>
          ))
        }
      </TransitionGroup>
    </div>
  )
};
