const HistoryItem = ({
  length, 
  index, 
  word,
}: {
  length: number, 
  index: number, 
  word: string,
}) => {

  const itemStyle: any = {
    width: "max-content", height: "auto",
    position: "relative", 
    fontSize: `${2 / Math.cbrt(length - index)}em`,
    opacity: 1 / Math.sqrt(length - index),
    transition: "all .3s ease-out",
  };

  const spanStyle: any = {
    position: "absolute",
    top: "0",
    left: "0",
  };

  return (
    <div 
      key={index}
      style={itemStyle}
    >
      <span
        style={spanStyle}
      >
        {word}
      </span>
    </div>
  )
}

export const History = ({history}: {history: Set<string>}) => (
  <div className="history">
    { 
      Array.from(history).map((word, index) => (
        <HistoryItem 
          length={history.size}
          index={index}
          word={word}
        />
      ))
    }
  </div>
);
