const HistoryItem = ({
  length, 
  index, 
  word,
}: {
  length: number, 
  index: number, 
  word: string,
}) => {
  const sizeFactor = 2 / Math.cbrt(length - index);
  const opacityFactor = 1 / Math.sqrt(length - index);

  return (
    <div 
      key={index}
      style={{
        fontSize: `${sizeFactor}em`,
        opacity: opacityFactor,
      }}
    >
      {word}
    </div>
  )
}

export const History = ({history}: {history: Set<string>}) => (
  <div className="history">
    { 
      Array.from(history).map((word, index) => (
        <HistoryItem 
          length={history.size}
          index={index}
          word={word}
        />
      ))
    }
  </div>
);
