import { useEffect, useRef, useState } from "react";
import { CSSTransition, TransitionGroup } from "react-transition-group";

const HistoryItem = ({
  length, 
  index, 
  word,
}: {
  length: number, 
  index: number, 
  word: string,
}) => {

  const reverseIndex = length - index;
  const isLast = (index === length - 1);

  const itemStyle: any = {
    opacity: 1 / Math.sqrt(reverseIndex),
    fontWeight: 600 / Math.sqrt(reverseIndex),
    fontSize: `${2 / Math.cbrt(reverseIndex)}em`,
    lineHeight: "1em",
    color: isLast ? "#ff4d91" : "white",
    transition: "all 300ms ease-out",
  };

  return (
    <div style={itemStyle} className="wobble">
      {word}
    </div>
  );
};

export const History = ({history}: {history: Set<string>}) => {

  // const nodeRef = useRef(null);
  // ref={nodeRef}

  return (
    <div className="history">
      <TransitionGroup>
        { 
          Array.from(history).map((word, index) => (
            <CSSTransition
              key={word}
              classNames="slide"
              timeout={{ enter: 300 }}
            >
              <HistoryItem 
                key={`history-${index}`}
                length={history.size}
                index={index}
                word={word}
              />
            </CSSTransition>
          ))
        }
      </TransitionGroup>
    </div>
  );
};
